import React,{ useEffect,useState } from "react";
import FoodItem from "./FoodItem";
import TotalCalories from "./TotalCalories";

function MealColumn({ title, fetchFoods, foods,onRemoveFoods,onUpdateQuantity, calculateTotalCalories }) {



const [quantity,setQuantity] = useState(100);


  useEffect(() => {
   fetchFoods();
    }
  ,[]);



  return (
    <div className="meal-column">
      <h2>{title}</h2>
      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th>Name</th>
            <th>Calories</th>
            <th>Proteins</th>
            <th>Carbs</th>
            <th>Fats</th>
            <th>Quantity(Grams)</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {foods.map((food,index) => (
            <tr key={index}>
              <td>{food['food_name']}</td>
              <td>{food['calories_100g']}</td>
              <td>{food['proteins']}</td>
              <td>{food['carbohydrates']}</td>
              <td>{food['fats']}</td>
              <td>
                <input
                  type="number"
                  min='0'
                  max='1000'
                  value={food.quantity}
                  onChange={(e) => setQuantity(e.target.value)
                }
                />
              </td>
              <td>
              <button onClick={() => onUpdateQuantity(food['food_name'])}>Update Quantity</button>
                <button onClick={() => onRemoveFoods(food['food_name'])}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <TotalCalories totalCalories />
      {/* Add button for navigating to the search page */}
    </div>
  );
}


export default MealColumn;
