import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function TableRow({ rowData }) {
  const [isOpen, setIsOpen] = useState(false);

  const toggleButtons = () => {
    setIsOpen(!isOpen);
  };



  return (
    <tr>
      <td>{rowData["name"]}</td>
      <td>{rowData["calories_100g"]}</td>
      <td>{rowData["proteins"]}</td>
      <td>{rowData["carbohydrates"]}</td>
      <td>{rowData["fats"]}</td>
      <td>
        <button className="btn btn-dark" onClick={toggleButtons}>
          Add to a Meal
        </button>
        {isOpen && (
          <div>
            <button className="btn btn-secondary" type="button" onClick>
              Breakfast
            </button>
            <button className="btn btn-secondary" type="button" onClick>
              Lunch
            </button>
            <button className="btn btn-secondary" type="button" onClick>
              Dinner
            </button>
          </div>
        )}
      </td>
    </tr>
  );
}

function FoodTable() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    if (searchTerm.trim() === "") {
      setSearchResults([]);
    } else {
      const searchTermLower = searchTerm.toLowerCase();
      await fetch(`http://localhost:8000/food/${searchTermLower}`)
        .then((response) => response.json())
        .then((data) => {
          setSearchResults(data);
        })
        .catch((error) => {
          console.error("Error fetching data:", error);
        });
    }
  };


  return (
    <div className="container mt-4">
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search for foods"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="input-group-append">
          <button className="btn btn-dark" type="button" onClick={handleSearch}>
            Search
          </button>
        </div>
      </div>
      <div>
        <table className="table">
          <thead className="thead-dark">
            <tr>
              <th>Name</th>
              <th>Calories (100g)</th>
              <th>Protein (100g)</th>
              <th>Carbs (100g)</th>
              <th>Fat (100g)</th>
            </tr>
          </thead>
          <tbody>
            {searchResults.map((food, index) => (
              <TableRow key={index} rowData={food} />
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default FoodTable;
