import './App.css';
import SearchBar from './Searchbar.js'
import { useState } from 'react';


function App() {

  const [data, setData] = useState({})

  const updateData = (searchParams) => {
    setData(searchParams);
  }
  return (
    <div className='container'>
      <div className='row mt-5'>
        <div className='col-4' />
        <h1 className='col'>Welcome to Easy Tracking!</h1>
      </div>
      <div className='row mt-5'>
      <div className='col-4'/>
        <p className='col-8'> This app was created in order to make it easier for people to track their daily calorie intake. here you can browse from a database of many types of foods and add it to your personal meals
          You can also calculate your pesonal BMR which will help you get to your daily calorie goals.
        </p>
      </div>
      <div className='row mt-5'>
        <SearchBar callback={updateData} />
      </div>
      <div className='row mt-5'>
        <p>Food: {"Name" in data ? data["Name"] : "No data to display"}</p>
        {data["Name"] && <button type='button'>Add to My meals</button>}</div>

    </div>
  );
}



export default App;
