import {useState} from 'react'
import MealColumn from './MealColumn';


function MyMeals() {

    const [breakfastFoods, setBreakfastFoods] = useState([]);
    const [lunchFoods, setLunchFoods] = useState([]);
    const [dinnerFoods, setDinnerFoods] = useState([]);

    // Function to calculate total calories for a meal column
    const calculateTotalCalories = (foods) => {
      return foods.reduce((totalCalories, food) => totalCalories + (food.calories * food.quantity), 0);
    };

    return (
      <div>
        <h1 className='text-center'>My Meals</h1>
        <div className="meal-columns">
          <MealColumn
            title="Breakfast"
            foods={breakfastFoods}
            setFoods={setBreakfastFoods}
            calculateTotalCalories={calculateTotalCalories}
          />
          <MealColumn
            title="Lunch"
            foods={lunchFoods}
            setFoods={setLunchFoods}
            calculateTotalCalories={calculateTotalCalories}
          />
          <MealColumn
            title="Dinner"
            foods={dinnerFoods}
            setFoods={setDinnerFoods}
            calculateTotalCalories={calculateTotalCalories}
          />
        </div>
      </div>
    );
  }

  export default MyMeals;
