import { useState } from "react";

function BMR() {
  const [age, setAge] = useState(25);
  const [weight, setWeight] = useState(70);
  const [height, setHeight] = useState(175);

  return (
    <div>
      <form className="form-control">
        <tr>
          <label for="age-field" style={{ marginRight: "10px" }}>Insert Age:</label>
          <input
            type="number"
            min="15"
            max="80"
            value={age}
            onChange={(e) => setAge(e.target.value)
            }
            style={{ marginRight: "20px" }}
          />
          <label for="weight-field" style={{ marginRight: "10px" }}>Insert Weight:</label>
          <input type="number"
          type="number"
          min="15"
          max="80"
          value={weight}
          onChange={(e) => setWeight(e.target.value)
          } style={{ marginRight: "20px" }} />
          <label for="height-field" style={{ marginRight: "10px" }}>Insert Height:</label>
          <input type="number" style={{ marginRight: "20px" }} />
          <button type="button">Calculate</button>
        </tr>
      </form>
    </div>
  );
}
export default BMR;
