import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function SearchBar() {
  const [SearchTerm, setSearchTerm] = useState("");
  const [foods,setFoods] = useState([]);
  const [filtered,setFiltered]= useState([]);


  const fetchData = () => {
    const mockData = [
        { name: "Apple", protein: 0.3, carbs: 25, fat: 0.4 },
        { name: "Banana", protein: 1.3, carbs: 27, fat: 0.4 },
        { name: "Chicken Breast", protein: 31, carbs: 0, fat: 3.6 },
        { name: "Apple-Pink Lady", protein: 0.2, carbs: 23, fat: 0.4 },
      ];

      setFoods(mockData);
  }

  const handleSearch = (food) => {
    food in fetchData() ? (<div>
        <p>
            {food.name, food.protein}
        </p>
    </div>) : (<p>food is not in the database</p>)
  }

  return (
    <div className="container">
      <form>
        <tr>
          <h2>Search for a Food</h2>
        </tr>
        <tr>
        <input className='form-control' id="search-text" placeholder="Search for a food" value={SearchTerm} onChange={(e) => setSearchTerm(e.target.value)}></input>
        <button type="button" onClick={handleSearch}>Search</button>
        </tr>

      </form>
    </div>
  );
}
export default SearchBar;
