import React,{useState} from 'react'
import MealColumn from './MealColumn';


function MyMeals() {

    const [breakfastFoods, setBreakfastFoods] = useState([]);
    const [lunchFoods, setLunchFoods] = useState([]);
    const [dinnerFoods, setDinnerFoods] = useState([]);

    const fetchFoods = async (meal_name) => {
      try {
        const response = await fetch(`http://localhost:8000/meals/${meal_name}`);
        if (response.ok) {
          const data = await response.json();
          if (meal_name === 'breakfast') setBreakfastFoods(data);
          else if (meal_name === 'lunch') setLunchFoods(data);
          else if (meal_name === 'dinner') setDinnerFoods(data);
        } else {
          console.log(`Error: ${response.status}, food does not exist in the database`);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
      console.log(breakfastFoods)
      console.log(lunchFoods)
      console.log(dinnerFoods)
    }

    // Function to remove a food item from the column
  const removeFood = async (food_name,meal_name) => {
    try {
      const response = await fetch(`http://localhost:8000${food_name}?meal_name=${meal_name}`)
      if (response.ok){
        switch(meal_name) {
          case 'breakfast':
            setBreakfastFoods(breakfastFoods.filter((_,food)=>food['food_name'] !== food_name));
            break;
          case 'lunch':
            setLunchFoods(lunchFoods.filter((_,food)=>food['food_name'] !== food_name));
            break;
          case 'dinner':
            setDinnerFoods(dinnerFoods.filter((_,food)=>food['food_name'] !== food_name))
        }
      }
    } catch(error) {
      console.error("Error removing food:", error);
    }
  };

    // Function to calculate total calories for a meal column
    //const calculateTotalCalories = (foods) => {
    //  return foods.reduce((totalCalories, food) => totalCalories + (food.calories * food.quantity), 0);
    //};

    return (
      <div>
        <h1 className='text-center'>My Meals</h1>
        <div className='row mt-5 text-center'>
            <div className='col'> 
            <MealColumn
            title="Breakfast"
            fetchFoods={()=>fetchFoods("breakfast")}
            foods = {breakfastFoods}
            //calculateTotalCalories={calculateTotalCalories}
          />
            </div>
            <div className='col'>
            <MealColumn
            title="Lunch"
            fetchFoods={()=>fetchFoods("lunch")}
            foods = {lunchFoods}
            //calculateTotalCalories={calculateTotalCalories}
          />
            </div>
            <div className='col'>
            <MealColumn
            title="Dinner"
            fetchFoods={()=>fetchFoods("dinner")}
            foods = {dinnerFoods}
            //calculateTotalCalories={calculateTotalCalories}
          />
            </div>
        </div>
      </div>
    );
  }

  export default MyMeals;
