import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function FoodTable() {
  const [foods, setFoods] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredFoods, setFilteredFoods] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const [isOpen, setIsOpen] = useState(false);

  const fetchData = () => {
    // Replace this with your actual API call to fetch data
    const mockData = [
      { name: "Apple", protein: 0.3, carbs: 25, fat: 0.4 },
      { name: "Banana", protein: 1.3, carbs: 27, fat: 0.4 },
      { name: "Chicken Breast", protein: 31, carbs: 0, fat: 3.6 },
      { name: "Apple-Pink Lady", protein: 0.2, carbs: 23, fat: 0.4 },
    ];
    setFoods(mockData);
    setFilteredFoods(mockData);
  };

  const handleSearch = () => {
    fetchData
    const filtered = foods.filter((food) =>
      food.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (filtered.length === 0) {
      setNotFound(true); // Set notFound to true if no results are found
    } else {
      setFilteredFoods(filtered);
      setNotFound(false); // Reset notFound if results are found
    }
    console.log(filtered);
  };

  const handleButtonClick = () => {
    setIsOpen(!isOpen); // Toggle the state isOpen
  };

  return (
    <div className="container mt-4">
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search for foods"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="input-group-append">
          <button className="btn btn-dark" type="button" onClick={handleSearch}>
            Search
          </button>
        </div>
      </div>
      <button className="btn btn-dark mb-3" onClick={fetchData}>
        Show All Foods
      </button>
      {notFound ? (
        <table className="table">
          <thead className="thead-dark">
            <tr>
              <th>Name</th>
              <th>Protein (100g)</th>
              <th>Carbs (100g)</th>
              <th>Fat (100g)</th>
            </tr>
          </thead>
          <tbody>
            {filteredFoods.map((food, index) => (
              <tr key={index}>
                <td>{food.name}</td>
                <td>{food.protein}</td>
                <td>{food.carbs}</td>
                <td>{food.fat}</td>
                <td>
                  <button
                    className="btn btn-secondary"
                    onClick={handleButtonClick}
                  >
                    Add to a Meal
                  </button>
                  {isOpen && (
                    <div>
                      <button className="btn btn-secondary" type="button">
                        Breakfast
                      </button>
                      <button className="btn btn-secondary" type="button">
                        Lunch
                      </button>
                      <button className="btn btn-secondary" type="button">
                        Dinner
                      </button>
                    </div>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No matching food found in the database.</p>
      )}
    </div>
  );
}

export default FoodTable;
