import React from 'react';
import FoodItem from './FoodItem';
import TotalCalories from './TotalCalories';


function MealColumn({ title, foods, setFoods, calculateTotalCalories }) {
    // Function to remove a food item from the column
    const removeFood = (index) => {
      const updatedFoods = [...foods];
      updatedFoods.splice(index, 1);
      setFoods(updatedFoods);
    };

    // Function to update quantity of a food item
    const updateQuantity = (index, newQuantity) => {
      const updatedFoods = [...foods];
      updatedFoods[index].quantity = newQuantity;
      setFoods(updatedFoods);
    };

    return (
      <div className="meal-column">
        <h2>{title}</h2>
        <ul>
          {foods.map((food, index) => (
            <FoodItem
              key={index}
              food={food}
              onRemove={() => removeFood(index)}
              onUpdateQuantity={(newQuantity) => updateQuantity(index, newQuantity)}
            />
          ))}
        </ul>
        <TotalCalories totalCalories={calculateTotalCalories(foods)} />
        {/* Add button for navigating to the search page */}
      </div>
    );
  }

  export default MealColumn;
