import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function FoodTable() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isOpen, setIsOpen] = useState(false);


  const mockData = [
      { name: "Apple", protein: 0.3, carbs: 25, fat: 0.4 },
      { name: "Banana", protein: 1.3, carbs: 27, fat: 0.4 },
      { name: "Chicken Breast", protein: 31, carbs: 0, fat: 3.6 },
      {name: "Apple-Pink Lady", protein:0.2, carbs: 23, fat: 0.4}
    ];


  const handleSearch = () => {

    if (searchTerm.trim() === '') {
      searchResults([]);
    }
    else {
      const searchResults = mockData.filter((food) => 
      food.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(searchResults);
    console.log(searchResults)
    }

  };

  const handleButtonClick = () => {
    setIsOpen(!isOpen); // Toggle the state isOpen
  };

  return (
    <div className="container mt-4">
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search for foods"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="input-group-append">
          <button
            className="btn btn-dark"
            type="button"
            onClick={handleSearch}
          >
            Search
          </button>
        </div>
      </div>
      <div>
      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th>Name</th>
            <th>Protein (100g)</th>
            <th>Carbs (100g)</th>
            <th>Fat (100g)</th>
          </tr>
        </thead>
        <tbody>
          {searchResults.map((food, index) => (
            <tr key={index}>
              <td>{food.name}</td>
              <td>{food.protein}</td>
              <td>{food.carbs}</td>
              <td>{food.fat}</td>
              <td><button className="btn btn-secondary" onClick={handleButtonClick}>Add to a Meal</button>
              {isOpen && (
        <div>
          <button className="btn "  type="button">Breakfast</button>
          <button className="btn"  type="button">Lunch</button>
          <button className="btn" type ='button'>Dinner</button>
        </div>
      )}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
    </div>
  );
}

export default FoodTable;
