import React,{ useEffect } from "react";
import FoodItem from "./FoodItem";
import TotalCalories from "./TotalCalories";

function MealColumn({ title, fetchFoods, foods, calculateTotalCalories }) {
  useEffect(() => {
    fetchFoods();
  }, []);

  // Function to remove a food item from the column
  const removeFood = () => {};

  // Function to update quantity of a food item
  const updateQuantity = (index, newQuantity) => {};

  return (
    <div className="meal-column">
      <h2>{title}</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Calories(100g)</th>
            <th>Proteins</th>
            <th>Carbs</th>
            <th>Fats</th>
            <th>Quantity</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {foods.map((food) => (
            <tr key={food.index}>
              <td>{food['name']}</td>
              <td>{food['calories_100g']}</td>
              <td>{food['proteins']}</td>
              <td>{food['carbohydrates']}</td>
              <td>{food['fats']}</td>
              <td>
                <input
                  type="number"
                  value={food['quantity']}
                  onChange={(e) => onUpdateQuantity(food.index, parseInt(e.target.value))}
                />
              </td>
              <td>
                <button onClick={() => onRemoveFood(food.index)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <TotalCalories totalCalories />
      {/* Add button for navigating to the search page */}
    </div>
  );
}


export default MealColumn;
