import {useState} from 'react'
import React from 'react';
import MealColumn from './MealColumn';


function MyMeals() {

    const [breakfastFoods, setBreakfastFoods] = useState([]);
    const [lunchFoods, setLunchFoods] = useState([]);
    const [dinnerFoods, setDinnerFoods] = useState([]);

    const fetchFoods = async (meal_name) => {
      await fetch(`http://localhost:8000/meals/${meal_name}`).then((response) => {
        if (response.ok) {
          console.log(response.status);
          response.json();

        } else
          console.log(
            ` ${response.status} ,food does not exist in the database`
          );
      }).then((data)=>{
        if (data.meal_name === 'breakfast')
          setBreakfastFoods(data)
        else if (data.meal_name === 'lunch')
          setLunchFoods(data)
        else if (data.meal_name === 'dinner')
          setDinnerFoods(data)
      }).catch((error) => {
        console.error("Error fetching data:", error);
      });
    }

    // Function to calculate total calories for a meal column
    const calculateTotalCalories = (foods) => {
      return foods.reduce((totalCalories, food) => totalCalories + (food.calories * food.quantity), 0);
    };

    return (
      <div>
        <h1 className='text-center'>My Meals</h1>
        <div className='row mt-5 text-center'>
            <div className='col'> 
            <MealColumn
            title="Breakfast"
            fetchFoods={fetchFoods("breakfast")}
            calculateTotalCalories={calculateTotalCalories}
          />
            </div>
            <div className='col'>
            <MealColumn
            title="Lunch"
            fetchFoods={fetchFoods("breakfast")}
            calculateTotalCalories={calculateTotalCalories}
          />
            </div>
            <div className='col'>
            <MealColumn
            title="Dinner"
            foods={dinnerFoods}
            setFoods={setDinnerFoods}
            calculateTotalCalories={calculateTotalCalories}
          />
            </div>
        </div>
      </div>
    );
  }

  export default MyMeals;
